// Extract channel ID from YouTube URL
const channelURL = $json.channelURL;
let channelID = '';

// Handle different YouTube URL formats
if (channelURL.includes('youtube.com/channel/')) {
  // Format: https://www.youtube.com/channel/UCxxxxxx
  channelID = channelURL.split('channel/')[1].split(/[?&/]/)[0];
} else if (channelURL.includes('youtube.com/c/')) {
  // Format: https://www.youtube.com/c/channelname
  // This requires an additional API call to resolve custom URL
  channelID = channelURL.split('/c/')[1].split(/[?&/]/)[0];
  // Mark as custom URL for later resolution
  return [{ json: { channelID: channelID, isCustomURL: true, API_KEY: $json.API_KEY } }];
} else if (channelURL.includes('youtube.com/@')) {
  // Format: https://www.youtube.com/@handle
  const handle = channelURL.split('/@')[1].split(/[?&/]/)[0];
  return [{ json: { channelID: '@' + handle, isHandle: true, API_KEY: $json.API_KEY } }];
} else if (channelURL.includes('youtube.com/user/')) {
  // Format: https://www.youtube.com/user/username
  const username = channelURL.split('/user/')[1].split(/[?&/]/)[0];
  return [{ json: { channelID: username, isUsername: true, API_KEY: $json.API_KEY } }];
} else if (channelURL.match(/^UC[a-zA-Z0-9_-]{22}$/)) {
  // Direct channel ID
  channelID = channelURL;
} else {
  throw new Error('Invalid YouTube channel URL format');
}

return [{ json: { channelID: channelID, API_KEY: $json.API_KEY } }];

// Extract uploads playlist ID
const items = $input.all();
if (!items[0]?.json?.items?.[0]?.contentDetails?.relatedPlaylists?.uploads) {
  throw new Error('Channel not found or no uploads playlist available');
}

const playlistId = items[0].json.items[0].contentDetails.relatedPlaylists.uploads;
return [{ json: { playlistId } }];

// Process playlist items and get video IDs
const items = $input.all();
const videos = [];

for (const item of items) {
  if (item.json?.items) {
    for (const video of item.json.items.slice(0, 10)) {
      if (video.snippet?.resourceId?.videoId) {
        videos.push({
          videoId: video.snippet.resourceId.videoId,
          title: video.snippet.title,
          thumbnail: video.snippet.thumbnails?.default?.url || video.snippet.thumbnails?.medium?.url,
          publishedAt: video.snippet.publishedAt,
          description: video.snippet.description
        });
      }
    }
  }
}

return videos.map(video => ({ json: video }));

// Process video statistics and calculate metrics
const items = $input.all();
const processedVideos = [];

for (const item of items) {
  if (item.json?.items?.[0]) {
    const video = item.json.items[0];
    const statistics = video.statistics || {};
    const snippet = video.snippet || {};
    const contentDetails = video.contentDetails || {};

    const views = parseInt(statistics.viewCount || '0', 10);
    const likes = parseInt(statistics.likeCount || '0', 10);
    const comments = parseInt(statistics.commentCount || '0', 10);

    const publishedAt = new Date(snippet.publishedAt);
    const now = new Date();
    const hoursSince = Math.max(1, (now - publishedAt) / (1000 * 60 * 60));
    const vph = parseFloat((views / hoursSince).toFixed(2));

    // Parse duration
    const duration = contentDetails.duration || 'PT0S';
    const match = duration.match(/PT(?:(\d+)H)?(?:(\d+)M)?(?:(\d+)S)?/);
    const hours = parseInt(match[1] || '0', 10);
    const minutes = parseInt(match[2] || '0', 10);
    const seconds = parseInt(match[3] || '0', 10);
    const totalSeconds = hours * 3600 + minutes * 60 + seconds;

    processedVideos.push({
      id: video.id,
      title: snippet.title,
      thumbnail: snippet.thumbnails?.medium?.url || snippet.thumbnails?.default?.url,
      viewCount: views,
      likeCount: likes,
      commentCount: comments,
      publishedAt: snippet.publishedAt,
      duration: totalSeconds,
      vph: vph,
      description: snippet.description || ''
    });
  }
}

processedVideos.sort((a, b) => new Date(b.publishedAt) - new Date(a.publishedAt));
const lasttop10 = processedVideos.slice(0, 10);

return { 
  json: { 
    success: true, 
    videos: lasttop10.sort((a, b) => b.vph - a.vph)
  } 
};

// Extract parameters and setup for image generation
const body = $json.body;
const selectedVideo = body.selectedVideo;
const selectedType = body.selectedType;
const selectedPrompt = body.selectedPrompt;

return [{
  json: {
    videoTitle: selectedVideo.title,
    videoId: selectedVideo.id,
    selectedType: selectedType,
    imagePrompt: selectedPrompt,
    videoData: selectedVideo
  }
}];

// Convert colored image to black and white
const coloredImageUrl = $json.url;
const revisedPrompt = $json.revised_prompt;

// Since we can't directly manipulate images in n8n without additional tools,
// we'll generate a B&W version using DALL-E with specific prompts
return [{
  json: {
    coloredImageUrl: coloredImageUrl,
    needsBWConversion: true,
    originalPrompt: $('Extract & Setup').item.json.imagePrompt,
    revisedPrompt: revisedPrompt,
    videoTitle: $('Extract & Setup').item.json.videoTitle
  }
}];

