// Combine both images data
const coloredImageData = $('Prepare B&W Generation').item.json;
const bwImageUrl = $json.url;

return [{
  json: {
    images: {
      colored: {
        url: coloredImageData.coloredImageUrl,
        type: 'colored'
      },
      blackWhite: {
        url: bwImageUrl,
        type: 'black_white'
      }
    },
    videoTitle: coloredImageData.videoTitle
  }
}];

// Process the merged data from images and script generation
const items = $input.all();
const originalData = $('Extract & Setup').item.json;

let imagesData = null;
let scriptData = null;

// Process merged items
for (let i = 0; i < items.length; i++) {
  const item = items[i];
  const data = item.json;

  // Check for images data
  if (data.images && data.images.colored && data.images.blackWhite) {
    imagesData = data.images;
  }

  // Check for script content
  if (data.message && data.message.content) {
    scriptData = {
      content: data.message.content,
      wordCount: data.message.content.split(/\s+/).length
    };
  }

  if (data.choices && data.choices[0]?.message?.content) {
    scriptData = {
      content: data.choices[0].message.content,
      wordCount: data.choices[0].message.content.split(/\s+/).length
    };
  }
}

// Combine all results
const finalResult = {
  success: true,
  timestamp: new Date().toISOString(),
  data: {
    video: {
      title: originalData?.videoTitle || '',
      id: originalData?.videoId || '',
      type: originalData?.selectedType || ''
    },
    generatedContent: {
      images: {
        colored: {
          url: imagesData?.colored?.url || '',
          type: 'colored'
        },
        blackWhite: {
          url: imagesData?.blackWhite?.url || '',
          type: 'black_white'
        }
      },
      script: {
        content: scriptData?.content || '',
        wordCount: scriptData?.wordCount || 0
      }
    },
    processingInfo: {
      completedAt: new Date().toISOString(),
      status: 'completed'
    }
  }
};

return { json: finalResult };
